import React from "react";
import Grid from '@mui/material/Unstable_Grid2';
import Box from '@mui/system/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';

function CadastrarDestinos(){

    const [nomeDestino, setNomeDestino] = React.useState("");
    const [, setEmail] = React.useState("");

    const [openMessage, setOpenMessage] = React.useState(false);
    const [messageText, setMessageText] = React.useState("");
    const [messageSeverity, setMessageSeverity] = React.useState("success");

    const [listaClientes, setListaClientes] = React.useState([]);

    React.useEffect(() => {
        getData();
    }, []);

    async function getData() {
        try {
            const res = await axios.get("/clientes");
            setListaClientes(res.data);
            console.log(res.data);
        } catch (error) {
            setListaClientes([]);
        }
    }

    function clearForm() {
        setNome("");
        setEmail("");
    }

    function handleCancelClick() {
        if (nome !== "" || email !== "") {
            setMessageText("Cadastro de cliente cancelado!");
            setMessageSeverity("warning");
            setOpenMessage(true);
        }
        clearForm();
    }

    async function handleSubmit() {
        if (nome !== "" && email !== "") {
            try {
                await axios.post("/cliente", {
                    nome: nome,
                    email: email,
                });
                console.log(`Nome: ${nome} - Email: ${email}`);
                setMessageText("Cliente cadastrado com sucesso!");
                setMessageSeverity("success");
                clearForm(); // limpa o formulário apenas se cadastrado com sucesso
            } catch (error) {
                console.log(error);
                setMessageText("Falha no cadastro do cliente!");
                setMessageSeverity("error");
            } finally {
                setOpenMessage(true);
                await getData();
            }
        } else {
            setMessageText("Dados de cliente inválidos!");
            setMessageSeverity("warning");
            setOpenMessage(true);
        }
    }

    function handleCloseMessage(_, reason) {
        if (reason === "clickaway") {
            return;
        }
        setOpenMessage(false);
    }

    return(
    <Box sx={{ flexGrow: 1 }}>
        <Grid container spacing={2} style={{margin:"10px", border:"1px solid lightGrey"}}>

            <Grid xs={12}>
                Nome do Destino:
                <TextField fullWidth size="small" label="Nome do destino" variant="outlined" />
            </Grid>

            <Grid xs={12}>
                País:
                <TextField fullWidth size="small" label="País" variant="outlined" />
            </Grid>

            <Grid xs={12}>
                Documentos Obrigatórios:
                <TextField fullWidth size="small" label="Documentos" variant="outlined" />
            </Grid>

            <Grid xs={12}>
                Descrição:
                <TextField fullWidth size="small" label="Descrição" variant="outlined" />
            </Grid>

            <Grid xs={12}>
                <Button variant="contained">Cadastrar</Button>
                <Button style={{marginLeft: "10px"}}variant="contained">Limpar</Button>
            </Grid>

        </Grid>
    </Box>

    );
}

export default CadastrarDestinos;
